														break;
									case 'b': (t->sval)[j] = 8;
														break;
									case 't': (t->sval)[j] = 9;
														break;
									case 'n': (t->sval)[j] = 10;
														break;
									case 'v': (t->sval)[j] = 11;
														break;
									case 'f': (t->sval)[j] = 12;
														break;
									case 'r': (t->sval)[j] = 13;
														break;
									case '"': (t->sval)[j] = 34;
														break;
									case '\'': (t->sval)[j] = 39;
														break;
									case '?': (t->sval)[j] = 63;
														break;
									case '\\': (t->sval)[j] = 92;
